class WarcProcessor:
    def __init__(self, shard_size=1000):
        self.kenlm_model_books = KenlmModel.from_pretrained('the_pile_books3', 'en')
        kenlm_model_en = load_kenlm_model("en", pretrained_models=["ccnet/wikipedia"])
      
        self.kenlm_model_en = kenlm_model_en['ccnet/wikipedia']
        self.proc = MultiPiiTextProcessor(lang=["en", "es", "ru", "de", "fr", "zh", "ar", "tr", "it", "hi"], config=configfile, 
                             keep_piic=False, debug=None)
        self.shard_size = shard_size

    def replace_personal_data(self, text):
        try:
            pii_cleaned_text = proc(' '.join(text), lang="en")
            
        except:
            pii_cleaned_text = ' '.join(text)
        return pii_cleaned_text
    
    def process_warcs(self, paths, output_path):
        for path in paths:
            self.process_warc(path, output_path)

    def switch_path(self, path):
        if "https://data.commoncrawl.org/" in path:
            path = path.replace("https://data.commoncrawl.org/", "s3://commoncrawl/")
        elif "s3://commoncrawl/" in path:
            path = path.replace("s3://commoncrawl/", "https://data.commoncrawl.org/")
        return path


    def process_warc(self, path, output_path, shard_size=500):

        while True:
            i = 0
            try:
                with fsspec.open(path, mode="rb", compression='gzip') as f:
                        i += 1
                        tf = io.BytesIO(f.read())
                        break
            except:
                if i % 10 == 0:
                    path = self.switch_path(path)
                    
                time.sleep(i)


        warchtml, warcimg, warcaud, warcvid, warciframe = (
            list(),
            list(),
            list(),
            list(),
            list(),
        )

        i = 0

        for index_r, record in tqdm(enumerate(ArchiveIterator(tf, max_content_length=4*1024**2))):
            try:
                if record.headers is None:
                    continue
                if record.http_headers is None:
                    continue
                if (
                    record.headers["WARC-Type"] == "response"
                    and record.content_length >= 128
                ):  
                    content_type = str(record.http_content_type).lower()
            
                    if content_type.startswith("text/html"):
        
                        url = str(record.headers["WARC-Target-URI"])
                    
                        html_bytes = record.reader.read()

                        # we get a bunch of dataframes and bildrecord and it's config
                        encoding = detect_encoding(html_bytes)
                        tree = HTMLTree.parse_from_bytes(html_bytes, encoding)
                        
                        del html_bytes

                        page_config, doc, imgs, vids, auds, iframedict = parser_bytes(url, tree)
                        if len(' '.join(doc) )<150 or filterout(' '.join(doc)):
                            continue

                        cleaned_text = self.replace_personal_data(doc)
                        perplexity_score_en = self.kenlm_model_en.get_perplexity(cleaned_text)
                        perplexity_score_books = self.kenlm_model_books.get_perplexity(cleaned_text)
                        full_html = str(tree.document)
                        if perplexity_score_books > 1200 and perplexity_score_en > 70_000:
                            continue
                        
                        lang = tree.document.query_selector('html').getattr('lang')
                        lang = lang if lang else detector(' '.join(doc))
                        lang = str(lang).replace("Language.","")

                        warchtml.append([url, page_config,doc,imgs,vids,auds,iframedict, full_html, lang, perplexity_score_en, perplexity_score_books])

                        i += 1

                        if i % shard_size == 0:
                            df = pd.DataFrame(warchtml, columns=["URL", "Page_config", "Doc", "Imgs","Vids","Auds","Iframedict", "full_html", "lang", "perplexity_score_en", "perplexity_score_books"])
                            del warchtml
                            df.to_parquet(f'{output_path}/BLID_Phase1_'+path.split("/")[-1].split(".")[-3]+f'_{i}.parquet', compression='gzip', engine='fastparquet')
                            warchtml = list()
                        del doc
                        del imgs
                        del vids
                        del auds
                        del iframedict
                        del full_html
                        gc.collect()

                        
            except Exception as er:
                continue
        
        df = pd.DataFrame(warchtml, columns=["URL", "Page_config", "Doc", "Imgs","Vids","Auds","Iframedict", "full_html", "lang", "perplexity_score_en", "perplexity_score_books"])
        del warchtml
        df.to_parquet(f'{output_path}/BLID_Phase1_'+path.split("/")[-1].split(".")[-3]+'.parquet', compression='gzip', engine='fastparquet')
        del df
